import sysRoleApi from '@/api/system/sys-role-api';
export default {
  name: 'SysRole',
  props: {
    // detail: 详细页面，add: 添加页面，update：编辑页面
    isDetail: {
      type: Boolean,
      default: false
    },
    isAdd: {
      type: Boolean,
      default: false
    },
    isUpdate: {
      type: Boolean,
      default: false
    }
  },
  data: function data() {
    return {
      dialogVisible: false,
      dialogTitle: null,
      form: {
        id: null,
        name: null,
        code: null,
        remark: null,
        state: 1
      },
      updateId: null,
      rules: {
        name: [{
          required: true,
          message: '请输入角色名称',
          trigger: 'blur'
        }, {
          min: 4,
          max: 16,
          message: '长度在 2 到 10 个字符',
          trigger: 'blur'
        }],
        code: [{
          required: true,
          message: '请输入角色编码',
          trigger: 'blur'
        }, {
          min: 4,
          max: 10,
          message: '长度在 4 到 20 个字符',
          trigger: 'blur'
        }],
        state: [{
          required: true,
          message: '请选择状态',
          trigger: 'change'
        }]
      }
    };
  },
  computed: {},
  created: function created() {},
  methods: {
    handle: function handle(id) {
      // alert(111)
      if (this.isDetail) {
        console.log('detail...' + id);
        this.rules = null;
        this.dialogTitle = '系统角色详情';
        this.getRoleDetail(id);
      } else if (this.isAdd) {
        console.log('add...');
        this.dialogTitle = '新增系统角色';
      } else if (this.isUpdate) {
        console.log('update...');
        this.dialogTitle = '修改系统角色';
        this.updateId = id;
        this.getRoleDetail(id);
      }

      this.dialogVisible = true;
    },
    submitAddForm: function submitAddForm() {
      var _this = this;

      this.$refs.sysRoleForm.validate(function (valid) {
        if (valid) {
          console.log('submit!');

          _this.addRole(); // this.$emit("getList")

        } else {
          console.log('error submit!!');
          return false;
        }
      });
    },
    submitUpdateForm: function submitUpdateForm() {
      var _this2 = this;

      this.$refs.sysRoleForm.validate(function (valid) {
        if (valid) {
          console.log('submit!');

          _this2.updateRole(); // this.$emit("getList")

        } else {
          console.log('error submit!!');
          return false;
        }
      });
    },
    getRoleDetail: function getRoleDetail(id) {
      var _this3 = this;

      sysRoleApi.detail(id).then(function (response) {
        if (response.code === 200) {
          _this3.form = response.data;
        }
      });
    },
    addRole: function addRole() {
      var _this4 = this;

      var addParam = {
        name: this.form.name,
        code: this.form.code,
        remark: this.form.remark,
        state: this.form.state
      };
      console.log(addParam);
      sysRoleApi.add(addParam).then(function (response) {
        if (response.code === 200) {
          _this4.restForm();

          _this4.$message({
            message: '保存系统角色成功',
            type: 'success'
          });

          _this4.$emit('change');
        }
      });
    },
    updateRole: function updateRole() {
      var _this5 = this;

      var updateParam = {
        id: this.updateId,
        name: this.form.name,
        code: this.form.code,
        remark: this.form.remark,
        state: this.form.state
      };
      sysRoleApi.update(updateParam).then(function (response) {
        if (response.code === 200) {
          _this5.restForm();

          _this5.$message({
            message: '修改系统角色成功',
            type: 'success'
          });

          _this5.$emit('change');
        }
      });
    },
    restForm: function restForm() {
      this.$refs.sysRoleForm.resetFields();
      this.dialogVisible = false;
    }
  }
};
