import request from '@/utils/request';
var uploadApi = {};
/**
 * 上传文件
 * @param data
 * @returns {AxiosPromise}
 */

uploadApi.upload = function (data) {
  return request({
    url: '/upload',
    method: 'post',
    data: data,
    headers: {
      'Content-Type': 'multipart/form-data'
    }
  });
};

export default uploadApi;
