import request from '@/utils/request';
var sysUserApi = {};
/**
 * 获取系统用户分页列表
 * @param data
 * @returns {AxiosPromise}
 */

sysUserApi.getPageList = function (data) {
  return request({
    url: '/sysUser/getPageList',
    method: 'post',
    data: data
  });
};
/**
 * 系统用户详情
 * @param data
 * @returns {AxiosPromise}
 */


sysUserApi.detail = function (id) {
  return request({
    url: '/sysUser/info/' + id,
    method: 'get'
  });
};
/**
 * 新增系统用户
 * @param data
 * @returns {AxiosPromise}
 */


sysUserApi.add = function (data) {
  return request({
    url: '/sysUser/add',
    method: 'post',
    data: data
  });
};
/**
