import request from '@/utils/request';
var deviceApi = {};
/**
 * 获取设备分页列表
 * @param data
 * @returns {AxiosPromise}
 */

deviceApi.getPageList = function (data, isAdmin) {
  var url = isAdmin ? '/deviceCommodity/getPageList' : '/deviceCommodity/getPageListByuser';
  return request({
    url: url,
    method: 'post',
    data: data
  });
};
/**
 * 设备详情
 * @param data
 * @returns {AxiosPromise}
 */


deviceApi.detail = function (id) {
  return request({
    url: '/deviceCommodity/info/' + id,
    method: 'get'
  });
};
/**
 * 新增设备
 * @param data
 * @returns {AxiosPromise}
 */


deviceApi.add = function (data) {
  return request({
    url: '/deviceCommodity/add',
    method: 'post',
    data: data
  });
};
/**
 * 修改设备
 * @param data
 * @returns {AxiosPromise}
 */


deviceApi.update = function (data) {
  return request({
    url: '/deviceCommodity/update',
    method: 'post',
    data: data
  });
};
/**
 * 删除设备
 * @param data
 * @returns {AxiosPromise}
 */


deviceApi.delete = function (id) {
  return request({
    url: '/deviceCommodity/delete/' + id,
    method: 'post'
  });
};

export default deviceApi;
