import TreeSelect from '@/components/TreeSelect'

import sysPermissionApi from '@/api/system/sys-permission-api'

export default {
  name: 'SysMenu',
  components: { TreeSelect },
  props: {
    // detail: 详细页面，add: 添加页面，update：编辑页面
    isDetail: {
      type: Boolean,
      default: false
    },
    isAdd: {
      type: Boolean,
      default: false
    },
    isUpdate: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      dialogVisible: false,
      innerVisible: false,
      dialogTitle: null,
      treeSelectData: null,
      selectMenuName: null,
      selectNode: null,
      selectData: null,
      menuIcon: '',
      form: {
        name: null,
        icon: null,
        code: null,
        url: null,
        type: null,
        parentId: null,
        level: 3,
        state: 1,
        sort: 0,
        remark: null
      },
      updateId: null,
      rules: {
        // TODO 菜单路径必须以/开头
        name: [
          { required: true, message: '请输入菜单名称', trigger: 'blur' },
          { min: 2, max: 10, message: '长度在 2 到 10 个字符', trigger: 'blur' }
        ],
        code: [
          { required: true, message: '请输入菜单编码', trigger: 'blur' },
          { min: 4, max: 50, message: '长度在 4 到 50 个字符', trigger: 'blur' }
        ],
        url: [
          { required: true, message: '请输入菜单路径', trigger: 'blur' },
          { min: 4, max: 100, message: '长度在 4 到 100 个字符', trigger: 'blur' }
        ],
        type: [
          { required: true, message: '请选择菜单类型', trigger: 'change' }
        ],
        state: [
          { required: true, message: '请选择状态', trigger: 'change' }
        ]
      }
    }
  },
  computed: {},
  created() {

  },
  methods: {
    handle(id) {
      if (this.isDetail) {
        this.dialogTitle = '系统菜单详情';
        this.getMenuDetail(id);
        this.rules = null;
      } else if (this.isAdd) {
        this.dialogTitle = '新增系统菜单';
      } else if (this.isUpdate) {
        this.dialogTitle = '修改系统菜单';
        this.updateId = id;
        this.getMenuDetail(id);
      }
      this.getNavMenu()
      this.dialogVisible = true
    },
    getNavMenu() {
      sysPermissionApi.getNavMenuTree().then(response => {
        this.treeSelectData = response.data;
        this.showParentId()
      });
    },
    handleMenuIcon() {
      if (this.isDetail) {
        return;
      }
      this.innerVisible = true;
    },
    choiceMenuIcon(icon) {
      this.menuIcon = icon;
      this.innerVisible = false;
      this.form.icon = icon;
    },
    showParentId() {
      this.$nextTick(() => {
        this.$refs.treeSelect.getNode(this.form.parentId)
      })
    },
    setParentIdAndLevel() {
      if (!this.form.parentId) {
        this.form.parentId = 0
      }
      if (!this.selectData) {
        this.form.level = 1
      } else if (this.selectData.level === 1) {
        this.form.level = 2
      } else if (this.selectData.level === 2) {
        this.form.level = 3
      }
    },
    submitAddForm() {
      this.$refs.sysMenuForm.validate((valid) => {
        if (valid) {
          console.log('submit!')
          this.addMenu()
        } else {
          console.log('error submit!!')
          return false
        }
      });
    },
    submitUpdateForm() {
      this.$refs.sysMenuForm.validate((valid) => {
        if (valid) {
          console.log('submit!')
          this.updateMenu()
          // this.$emit("getList")
        } else {
          console.log('error submit!!')
          return false
        }
      });
    },
    getMenuDetail(id) {
      sysPermissionApi.detail(id).then(response => {
        if (response.code === 200) {
          this.form = response.data;
          this.menuIcon = this.form.icon;
        }
      });
    },
    addMenu() {
      this.setParentIdAndLevel()
      const addParam = {
        name: this.form.name,
        code: this.form.code,
        icon: this.form.icon,
        url: this.form.url,
        type: this.form.type,
        parentId: this.form.parentId,
        level: this.form.level,
        sort: this.form.sort,
        state: this.form.state,
        remark: this.form.remark
      }
      sysPermissionApi.add(addParam).then(response => {
        if (response.code === 200) {
          this.restForm();
          this.$message({
            message: '保存系统菜单成功',
            type: 'success'
          })
          this.$emit('change')
        }
      })
    },
    updateMenu() {
      this.setParentIdAndLevel()
      const updateParam = {
        id: this.updateId,
        name: this.form.name,
        code: this.form.code,
        icon: this.form.icon,
        url: this.form.url,
        type: this.form.type,
        parentId: this.form.parentId,
        level: this.form.level,
        sort: this.form.sort,
        state: this.form.state,
        remark: this.form.remark
      }
      sysPermissionApi.update(updateParam).then(response => {
        if (response.code === 200) {
          this.restForm();
          this.$message({
            message: '修改系统菜单成功',
            type: 'success'
          })
          this.$emit('change')
        }
      })
    },
    restForm() {
      this.$refs.sysMenuForm.resetFields()
      this.dialogVisible = false
    }
  }
}
