import deviceApi from '@/api/device/device-list-api'
import UserDialog from './userDialog'

export default {
  name: 'DeviceDialog',
  components: {
    UserDialog
  },
  props: {
    // detail: 详细页面，add: 添加页面，update：编辑页面
    isDetail: {
      type: Boolean,
      default: false
    },
    isAdd: {
      type: Boolean,
      default: false
    },
    isUpdate: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      dialogVisible: false,
      dialogTitle: null,
      form: {
        id: null,
        username: null,
        dname: null,
        did: null,
        remarks: null
        // state: 1
      },
      updateId: null,
      rules: {
        dname: [
          { required: true, message: '请输入设备名称', trigger: 'blur' }
          // { required: true, message: '请输入设备名称', trigger: 'blur' }
          // { min: 4, max: 16, message: '长度在 2 到 10 个字符', trigger: 'blur' }
        ],
        did: [
          { required: true, message: '请输入设备编号', trigger: 'blur' }
        ]
        // state: [
        //   { required: true, message: '请选择状态', trigger: 'change' }
        // ]
      }

    }
  },
  computed: {},
  created() {
  },
  methods: {
    handle(id) {
      // alert(111)
      if (this.isDetail) {
        console.log('detail...' + id)
        this.rules = null;
        this.dialogTitle = '设备详情';
        this.getRoleDetail(id);
      } else if (this.isAdd) {
        console.log('add...')
        this.dialogTitle = '新增设备';
      } else if (this.isUpdate) {
        console.log('update...')
        this.dialogTitle = '修改设备';
        this.updateId = id;
        this.getRoleDetail(id);
      }
      this.dialogVisible = true
    },
    submitAddForm() {
      this.$refs.DeviceDialogForm.validate((valid) => {
        if (valid) {
          console.log('submit!')
          this.addRole()
          // this.$emit("getList")
        } else {
          console.log('error submit!!')
          return false
        }
      });
    },
    submitUpdateForm() {
      this.$refs.DeviceDialogForm.validate((valid) => {
        if (valid) {
          console.log('submit!')
          this.updateRole()
          // this.$emit("getList")
        } else {
          console.log('error submit!!')
          return false
        }
      });
    },
    getRoleDetail(id) {
      deviceApi.detail(id).then(response => {
        if (response.code === 200) {
          this.form = response.data;
        }
      });
    },
    addRole() {
      const addParam = {
        did: this.form.did,
        dname: this.form.dname,
        remarks: this.form.remarks
        // state: this.form.state
      }
      console.log(addParam)
      deviceApi.add(addParam).then(response => {
        if (response.code === 200) {
          this.restForm();
          this.$message({
            message: '保存设备成功',
            type: 'success'
          })
          this.$emit('change')
        }
      })
    },
    updateRole() {
      const updateParam = {
        id: this.updateId,
        did: this.form.did,
        // username: this.form.username,
        dname: this.form.dname,
        remarks: this.form.remarks
        // state: this.form.state
      }
      deviceApi.update(updateParam).then(response => {
        if (response.code === 200) {
          this.restForm();
          this.$message({
            message: '修改设备成功',
            type: 'success'
          })
          this.$emit('change')
        }
      })
    },
    restForm() {
      this.$refs.DeviceDialogForm.resetFields()
      this.dialogVisible = false
    },
    handOpenUserDialog() {
      this.$nextTick(() => {
        this.$refs.userDialog.handle()
      });
    },
    handleSave(row) {
      console.log(row, '-=-=-=-=-=-=')
    },
  }
}
